w:r.w:t:
figure;
hold off;
n = 100;
theta = linspace(0,2*pi,n);
r = 3; % radius
x = r*cos(theta);
y = r*sin(theta);
plot(x,y,'g-');
hold on;
plot(0,0,'k*');
axis equal;
title(sprintf("Circle of radius %d", r));

figure;
hold off;
x = linspace(0,10,n); % Input vector
a = 1; % Parabola parameter
y = sqrt(4*a*x); % Equation of the parabola
plot(x,y,'r-',x,-y,'r-');
hold on;
plot(a,0,'k*');
xlim([-1 10]);
ylim([-8 8]);
str = sprintf('Parabola with focus at (%f, 0) and the directrix as the line x = %f', a, -a);
title(str);

figure;
hold off;
theta = linspace(0,2*pi,n);
a = 3;    % semi major axis
b = 2;    % semi minor axis
x = a*cos(theta);
y = b*sin(theta);
plot(x,y,'b-');
hold on;
% Compute the eccentricity for plotting foci
e = sqrt((1 - b^2/a^2));    % eccentricity
plot(a*e,0,'k*', -a*e,0,'k*')
title('Ellipse with major and minor axes along the Cartesian axes');
